import React from 'react';
import { Query } from 'react-apollo';
import { useQuery } from 'react-apollo';
import { gql } from 'apollo-boost';

const GET_USERS = gql`
  query GetGreeting($userid: Int!) {
        user(id: $userid) {
        id
        name
        age
        jobs{
            company
            duration
        }
        }
  }
`;
function Users (user) {
    const { loading, error, data } = useQuery(GET_USERS, {
        variables: { userid: parseInt(user.id) },
    });
    if (loading) return <tr><td>Loading...</td></tr>;
    return (
        <tr key={data.user.id}>
            <td>{data.user.id}</td>
            <td>{data.user.name}</td>
            <td>{data.user.age}</td>
            <td>{data.user.jobs.company}</td>
            <td>{data.user.jobs.duration}</td>
        </tr>
    );
}
const User = (Users) => (
    <Query
        query={gql`
        {
                user(id: Users) {
                id
                name
                age
                jobs{
                    company
                    duration
                }
                }
            }
    `}
    >
        {({ loading, error, data }) => {
            console.log(Users.id);
            if (loading) return <tr><td>Loading...</td></tr>;
            if (error) return <tr><td>Error :( </td></tr>;
            return (
                <tr key={data.user.id}>
                    <td>{data.user.id}</td>
                    <td>{data.user.name}</td>
                    <td>{data.user.age}</td>
                    <td>{data.user.jobs.company}</td>
                    <td>{data.user.jobs.duration}</td>
                </tr>
            );
        }}
    </Query>
);
export default Users;
