import React from 'react';
import { Query } from "react-apollo";
import gql from "graphql-tag";

const User = () => (
    <Query
        query={gql`
      {
        user(id: 1) {
          id
          name
          age
          jobs{
              company
              duration
          }
        }
      }
    `}
    >
        {({ loading, error, data }) => {
            if (loading) return <p>Loading...</p>;
            if (error) return <p>Error :(</p>;
            return (
                <div key={data.user.id}>
                    <p>{`${title} by ${author}`}</p>
                </div>
            );
        }}
    </Query>
);
export default User;
