import React from 'react';
import { Query } from "react-apollo";
import gql from "graphql-tag";

const User = () => (
    <Query
        query={gql`
      {
        user(id: 1) {
          id
          name
          age
          jobs{
              company
              duration
          }
        }
      }
    `}
    >
        {({ loading, error, data }) => {
            if (loading) return <p>Loading...</p>;
            if (error) return <p>Error :(</p>;
            return (
                <tr key={data.user.id}>
                    <td id="id"></td>
                    <td id="name"></td>
                    <td id="age"></td>
                    <td id="company"></td>
                    <td id="Experience"></td>
                </tr>
            );
        }}
    </Query>
);
export default User;
